mapper.delete:
delete from COMMENT
where id = #{id}
or (PARENT_ID = #{id} and TYPE = 2)

mapper.update:
update COMMENT
set COMMENT_COUNT = COMMENT_COUNT

    + #{commentCount}


    - #{commentCount}

where id = #{id}

update COMMENT
set LIKE_COUNT = LIKE_COUNT

    + #{likeCount}


    - #{likeCount}

where id = #{id}
